import React, { useRef } from "react";
import DraggableContainer from "./DraggableContainer";
import Widget from "./Widget";

const WIDGETLIST = [
  { id: "A", content: "A", size: 1 },
  { id: "B", content: "B", size: 2 },
  { id: "C", content: "C", size: 3 },
  { id: "D", content: "D", size: 4 },
];

const dashBroad = {
  display: "grid",
  gridTemplateColumns: "auto auto auto",
  gridGap: "1rem",
};

function App() {
  const draggedOverContainerId = useRef(null);

  const handleDragEntered = (dragContainerId) => {
    draggedOverContainerId.current = dragContainerId;
  };
  //const [draggedOverContainerId, setDraggedOverContainerId] = useState(null);

  return (
    <div style={dashBroad}>
      {WIDGETLIST.map((w) => (
        <DraggableContainer
          key={w.id}
          size={w.size}
          onDragEnter={() => handleDragEntered(w.id)}
          isDraggedOver={w.id === draggedOverContainerId.current}
        >
          <Widget content={w.content} />
        </DraggableContainer>
      ))}
    </div>
  );
}

export default App;
